mapper.select:
select * from note 
where uid = #{uid}
	and tno = #{tno} 
order by tq_no

select * from note 
where uid = #{uid} 
	and tno = #{tno} 
	and tq_no = #{tq_no}
order by note_date desc limit 1

mapper.update:
update note 
set note_content = #{note_content}, 
	note_memo = #{note_memo},
	note_date = #{note_date}
where note_no = #{note_no}
