mapper.select:
select * from testquestion 
where tno = #{tno}
order by tq_small_no

select * from testquestion
where tno = #{testName.tno} 
	and tq_subject_no = #{tq_subject_no}
order by tq_small_no

select * from testquestion
where tno = #{tno} 
	and tq_small_no = #{tq_small_no}

select count(*) from testquestion 
where tno = #{tno}
	 and tq_subject = #{tq_subject}
